	public boolean addAll(Collection<? extends E> c) {
		for (E element : c) {
			MyNode<E> node = new MyNode<E>(element);
			queue.setNext(node);
			node.setPrevious(queue);
			queue = node;
			size++;
		}
		return true;
	}

